"use strict";
function exibirPessoa(nome, idade) {
    console.log("O nome \u00E9 " + nome + " e tem " + idade + " anos.");
}
function exibirAnimal(nome, idade, especie) {
    console.log("O nome \u00E9 " + nome + ", " + especie + " e tem " + idade + " anos.");
}
exibirPessoa('Marina', 9);
exibirAnimal('simba', 10, 'mamifero');
exibirAnimal('simba', 10, 'mamifero');
